(3, 'owner-wall', '{error}\r\n\r\n<div style="margin:auto; width:565px;">\r\n  <img src="images/wall/message_top_bg.png"  id="top_img" />\r\n    <div style="background:url(/images/wall/message_middle_bg.png); padding-left:95px;">\r\n	<div class="formField rsform-block rsform-block-message">\r\n		<strong>Message</strong><br/>\r\n		{message:body}<br/>\r\n		{message:validation}\r\n	</div>\r\n	<div class="formField rsform-block rsform-block-attachments">\r\n		<strong>Add a File</strong><br/>\r\n		{attachments:body}<br/><br/>\r\n		{attachments:validation}\r\n	</div>\r\n	<div class="formField rsform-block rsform-block-submit">\r\n		{submit:body}<br/>\r\n	</div>\r\n	<div class="formField rsform-block rsform-block-articleid">\r\n		{article_id:body}\r\n	</div>\r\n	<div class="formField rsform-block rsform-block-cssclass">\r\n		{cssclass:body}\r\n	</div>\r\n	<div class="formField rsform-block rsform-block-userid">\r\n		{user_id:body}\r\n	</div>\r\n        <div class="formField rsform-block rsform-block-userid">\r\n		{type_id:body}\r\n	</div>\r\n       <div class="formField rsform-block rsform-block-userid">\r\n		{created_on:body}\r\n	</div>\r\n    </div>\r\n  <img src="images/wall/message_bottom_bg.png" id="bottom_img" />\r\n</div>\r\n', '2lines', 0, '', '', 'Owner Wall', 1, 'en-GB', '', 1, '<p>Posted to Job Card successfully</p>', 0, '', '', '', '', '', '', '', '', 1, 0, '', '', '', '', '', '', '', '', '', 1, '//$_POST[''form''][''created_on'']= date("D M j, y g:i a (e)");\r\n$_POST[''form''][''created_on'']= date("m/d/Y");\r\nrequire_once(JPATH_THEMES.DS.''jreviews_overrides'' . DS . ''helpers''. DS . ''wall.php'');\r\n$wall = new JGWallHelper();\r\nif ($wall->isServiceProvider()):\r\n  if($wall->isProjectManager()):\r\n    $_POST[''form''][''cssclass''] = ''post-sp post-pm'';\r\n  else:\r\n    $_POST[''form''][''cssclass''] = ''post-sp'';\r\n  endif;\r\nendif;\r\n\r\n$article_id = $_POST[''form''][''article_id''];\r\n$user_id = $_POST[''form''][''user_id''];\r\n$db =& JFactory::getDBO();\r\n$query = "INSERT INTO #__probid_wall_notifications(article_id, user_id) VALUES (";\r\n$query .= $article_id . ", " . $user_id .")";\r\n$db->setQuery($query);\r\n$db->query();', '//RUN PHP SCRIPT TO COPY FILE OVER TO AMAZON S3 UPON FORM SUBMISSION\r\n//http://dev.probiddirect.com/custom/amazon-scripts/uploadToS3.php with file name\r\n//THEN DELETE LOCAL COPY UPON SUCCESS\r\n//IF ERROR THEN EMAIL ADMIN@PROBIDDIRECT.COM', '', '', '', '', 0, '', '', '(*)', '<p class="formRed">Please complete all required fields</p>', '\\n', 1, '', 'userForm', '', '', '', 1, '', 1, 0, 0),
(11, 'advancedsearchform copy', '<h2>Advanced Search: Projects</h2>\r\n{error}\r\n<div id="spinning-wheel" class="hidden">Loading...</div>\r\n<div id=''tagsDiv''><ul id=''tags-ul''></ul></div>\r\n<div id=''formDiv''>\r\n<table border="0">\r\n	<tr class="rsform-block rsform-block-category">\r\n		<td>{Category:caption}</td>\r\n		<td>{Category:body}<div class="formClr"></div>{Category:validation}</td>\r\n		<td>{Category:description}</td>\r\n	</tr>\r\n	<tr class="rsform-block rsform-block-time" id=''timeTR''>\r\n		<td>{time:caption}</td>\r\n		<td>{time:body}<div class="formClr"></div>{time:validation}</td>\r\n		<td>{time:description}</td>\r\n	</tr>\r\n	<tr class="rsform-block rsform-block-tags">\r\n		<td>{Tags:caption} <span style=''color:red''>(*)</span></td>\r\n		<td>{Tags:body}<div class="formClr"></div>{Tags:validation}</td>\r\n		<td>{Tags:description}</td>\r\n	</tr>\r\n	<tr class="rsform-block rsform-block-keywords">\r\n		<td>{Keywords:caption}</td>\r\n		<td>{Keywords:body}<div class="formClr"></div>{Keywords:validation}</td>\r\n		<td>{Keywords:description}</td>\r\n	</tr>\r\n	<tr class="rsform-block rsform-block-zipcode">\r\n		<td>{Zipcode:caption} <span style=''color:red''>(*)</span></td>\r\n		<td>{Zipcode:body}<div class="formClr"></div>{Zipcode:validation}</td>\r\n		<td>{Zipcode:description}</td>\r\n	</tr>\r\n	<tr class="rsform-block rsform-block-radius" id=''radiusTR''>\r\n		<td>{Radius:caption}</td>\r\n		<td>{Radius:body}<div class="formClr"></div>{Radius:validation}</td>\r\n		<td>{Radius:description}</td>\r\n	</tr>\r\n	<tr class="rsform-block rsform-block-submit">\r\n		<td>{submit:caption}</td>\r\n		<td>{submit:body}<div class="formClr"></div>{submit:validation}</td>\r\n		<td>{submit:description}</td>\r\n	</tr>\r\n        <tr class="rsform-block">\r\n          <td colspan=''3''><span style=''color:red''>(*) is required</span></td>\r\n        </tr>\r\n</table>\r\n</div>', 'inline', 0, '<style>\r\n#formDiv {\r\n  width: 50%;\r\n  float: left;\r\n}\r\n#tagsDiv {\r\n  width: 45%;\r\n  float: right;\r\n}\r\n</style>', '<script type="text/javascript" src="/components/com_jreviews/jreviews/views/js/jquery/jquery-1.7.1.min.js"></script>\r\n<script type="text/javascript">\r\n//set defaults\r\nvar as_category_id = 78;\r\nvar as_tags = "";\r\nvar as_keywords = "";\r\nvar as_zipcode = 95928;\r\nvar as_radius = 30;\r\nvar as_page = 1;\r\nvar as_limit = 10;\r\nvar as_time = 0;\r\njQuery(function() {\r\n  jQuery(''#userForm'').submit(function() {\r\n          //reset page numbers if they are clicking submit button\r\n          as_page = 1;\r\n	  return advancedSearchRequest(as_page);\r\n  });//ends userForm submit\r\n});//ends init jQuery document ready\r\nfunction advancedSearchRequest(pageSet) {\r\n	as_page = pageSet;\r\n    if(jQuery(''#Zipcode'').val() == '''') {\r\n      alert(''You must provide a zipcode'');\r\n      jQuery(''#Zipcode'').focus();\r\n      return false;\r\n    }\r\n//populate our object prior to sending and do some error checking\r\n    if(jQuery(''#Tags'').val() != null) {\r\n      if(jQuery(''#Tags'').val().length >= 50) {\r\n        alert(''You have chosen too many tags, choose no more than 50'');\r\n        jQuery(''#Tags'').focus();\r\n        return false;\r\n      }\r\n      //clear out last search\r\n      jQuery(''#spinning-wheel'').removeClass(''hidden'');\r\n      //reset tags from last request\r\n      as_tags = "";\r\n      jQuery.each(jQuery(''#Tags'').val(), function(index, value) {\r\n        as_tags += value + ",";\r\n      });\r\n      as_category_id = jQuery(''#Category'').val();\r\n      as_keywords = jQuery(''#Keywords'').val(); \r\n      as_zipcode = jQuery(''#Zipcode'').val();\r\n      as_radius = jQuery(''#Radius'').val();\r\n      as_time = jQuery(''#time'').val();\r\n      var form = {\r\n        category_id : as_category_id,\r\n        tags : as_tags,\r\n        keywords : as_keywords,\r\n        zipcode : as_zipcode,\r\n        radius : as_radius,\r\n        page : as_page,\r\n        